/**
 * 加载所有分类的商品
 */
loadAllGoods() {
  this.setData({ loading: true })

  // 模拟加载所有分类商品数据
  setTimeout(() => {
    const categoryGoodsList = this.getAllCategoryGoods()
    this.setData({
      categoryGoodsList,
      loading: false
    })

    // 延迟计算各分类区域位置
    setTimeout(() => {
      this.calculateCategoryTops()
    }, 100)
  }, 500)
},

