  return categories.map((category, index) => ({
    ...category,
    categoryIndex: index,
    goods: allGoods[index] || []
  }))
},

/**
 * 计算各分类区域的位置
 */
calculateCategoryTops() {
  const query = wx.createSelectorQuery()
  query.selectAll('.category-section').boundingClientRect()
  query.select('.goods-list').boundingClientRect()
  query.exec((res) => {
    if (res[0] && res[1]) {
      const sections = res[0]
      const container = res[1]
      const categoryTops = sections.map(item => item.top - container.top)
      this.setData({ categoryTops })
    }
  })
},

/**
 * 右侧商品列表滚动事件
 */
onGoodsScroll(e) {
  if (this.scrollLock) return // 点击左侧分类时锁定，避免冲突

  const { scrollTop } = e.detail
  const { categoryTops, currentCategory } = this.data

  // 找到当前滚动位置对应的分类
  let newCategory = 0
  for (let i = 0; i < categoryTops.length; i++) {
    if (scrollTop >= categoryTops[i] - 10) {
      newCategory = i
    } else {
      break
    }
  }

  if (newCategory !== currentCategory) {
    this.setData({ currentCategory: newCategory })
    // 让左侧分类滚动到可视区域
    this.scrollLeftCategory(newCategory)
  }
},

/**
 * 左侧分类滚动到指定位置
 */
scrollLeftCategory(index) {
  this.setData({
    scrollToView: `category-${index}`
  })
},

