// pages/rider/supermarket/index.js

Page({
  data: {
    // 搜索
    searchValue: '',
    currentLocation: '校园超市',

    // 分类
    categories: [
      { id: 1, name: '热销推荐', icon: '', badge: '' },
      { id: 2, name: '零食饮料', icon: '', badge: '热' },
      { id: 3, name: '方便速食', icon: '', badge: '' },
      { id: 4, name: '日用百货', icon: '', badge: '' },
      { id: 5, name: '个护美妆', icon: '', badge: '' },
      { id: 6, name: '文具办公', icon: '', badge: '' },
      { id: 7, name: '数码配件', icon: '', badge: '' },
      { id: 8, name: '生鲜水果', icon: '', badge: '新' },
      { id: 9, name: '乳品早餐', icon: '', badge: '' },
      { id: 10, name: '冷饮冰品', icon: '', badge: '' }
    ],
    currentCategory: 0,
    scrollToView: '', // 左侧分类滚动定位
    rightScrollTop: 0, // 右侧滚动位置

    // 商品列表（按分类分组）
    categoryGoodsList: [],
    loading: false,
    loadingMore: false, // 加载更多状态

    // 分类区域高度信息
    categoryTops: [],

    // 购物车
    cartList: [],
    cartTotalCount: 0,
    cartTotalPrice: '0.00',
    showCart: false,

    // 配送
    deliveryFee: 2,
    minOrderAmount: 15
  },

  // 滚动锁定标志
  scrollLock: false,
  // 每个分类的分页信息
  categoryPageInfo: {},
  // 每页加载数量
  pageSize: 10,
  // 初始加载的分类数量
  initialLoadCount: 3,
  // 已加载的分类索引集合
  loadedCategories: new Set(),

  onLoad() {
    this.initCategoryPageInfo()
    this.loadInitialCategories()
  },

  onShow() {
    const cartList = wx.getStorageSync('supermarket_cart') || []
    this.setData({ cartList })
    this.calculateCart()
  },

  /**
   * 初始化分类分页信息
   */
  initCategoryPageInfo() {
    const { categories } = this.data
    this.categoryPageInfo = {}
    categories.forEach((_, index) => {
      this.categoryPageInfo[index] = {
        page: 1,
        hasMore: true,
        loading: false
      }
    })
  },

  /**
   * 初始加载前几个分类的数据
   */
  loadInitialCategories() {
    this.setData({ loading: true })

    const { categories } = this.data
    const initialList = categories.map((category, index) => ({
      ...category,
      categoryIndex: index,
      goods: [],
      hasMore: true,
      loading: false
    }))

    this.setData({ categoryGoodsList: initialList })

    // 只加载前几个分类的第一页数据
    const loadPromises = []
    for (let i = 0; i < Math.min(this.initialLoadCount, categories.length); i++) {
      loadPromises.push(this.loadCategoryGoods(i, true))
    }

    Promise.all(loadPromises).then(() => {
      this.setData({ loading: false })
      setTimeout(() => {
        this.calculateCategoryTops()
      }, 300)
    })
  },

  /**
   * 加载指定分类的商品（支持分页）
   * @param {number} categoryIndex 分类索引
   * @param {boolean} isInitial 是否初始加载
   */
  loadCategoryGoods(categoryIndex, isInitial = false) {
    return new Promise((resolve) => {
      const pageInfo = this.categoryPageInfo[categoryIndex]
      if (!pageInfo || pageInfo.loading || (!isInitial && !pageInfo.hasMore)) {
        resolve()
        return
      }

      pageInfo.loading = true
      this.loadedCategories.add(categoryIndex)

      // 更新UI加载状态
      const key = `categoryGoodsList[${categoryI.loading`
      this.setData({ [key]: true })

      // 模拟API请求
      setTimeout(() => {
        const newGoods = this.fetchCategoryGoods(categoryIndex, pageInfo.page)
        const { categoryGoodsList } = this.data

        // 合并商品数据
        const currentGoods = categoryGoodsList[categoryIndex].goods || []
        const updatedGoods = [...currentGoods, ...newGoods]

        // 判断是否还有更多
        const hasMore = newGoods.length >= this.pageSize

        // 更新数据
        this.setData({
          [`categoryGoodsList[${categoryIndex}].goods`]: updatedGoods,
          [`categoryGoodsList[${categoryIndex}].hae`]: hasMore,
          [`categoryGoodsList[${categoryIndex}].loading`]: false
        })

        // 更新分页信息
        pageInfo.page++
        pageInfo.hasMore = hasMore
        pageInfo.loading = false

        // 重新计算位置
        setTimeout(() => {
          this.calculateCategoryTops()
        }, 100)

        resolve()
      }, 300)
    })
  },

  /**
   * 模拟获取分类商品数据（实际项目中替换为API调用）
   */
  fetchCategoryGoods(categoryIndex, page) {
    // 模拟数据源
    const allGoods = {
      0: this.generateMockGoods('热销', 101, 50),
      1: this.generateMockGoods('零食', 201, 40),
      2: this.generateMockGoods('速食', 301, 35),
      3: this.generateMockGoods('日用', 401, 45),
      4: this.generateMockGoods('个护', 501, 25),
      5: this.generateMockGoods('文具', 601, 30),
      6: this.generateMockGoods('数码', 701, 20),
      7: this.generateMockGoods('水果', 801, 28),
      8: this.generateMockGoods('乳品', 901, 22),
      9: this.generateMockGoods('冷饮', 1001, 18)
    }

    const goods = allGoods[categoryIndex] || []
    const start = (page - 1) * this.pageSize
    const end = start + this.pageSize

    return goods.slice(start, end)
  },

  /**
   * 生成模拟商品数据
   */
  generateMockGoods(prefix, startId, total) {
    const goods = []
    const tags = ['热销', '新品', '爆款', '特价', '']
    const images = [
      'https://img.alicdn.com/imgextra/i4/2616970884/O1CN01FpfRzj1IOuv0xPJKT_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i3/2616970884/O1CN01qNxfnB1IOuuzWJHxK_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i1/2616970884/O1CN01Ej5Yzd1IOuv2OQHQE_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i2/2616970884/O1CN01xNxfnB1IOuuzWJHxL_!!2616970884.jpg'
    ]

    for (let i = 0; i < total; i++) {
      const price = (Math.random() * 50 + 2).toFixed(2)
      const hasOriginal = Mrandom() > 0.5
      goods.push({
        id: startId + i,
        name: `${prefix}商品${i + 1}`,
        price: price,
        originalPrice: hasOriginal ? (parseFloat(price) * 1.2).toFixed(2) : '',
        image: images[i % images.length],
        tag: tags[Math.floor(Math.random() * tags.length)],
        sales: Math.floor(Math.random() * 1000),
        desc: `${prefix}商品描述${i + 1}`
      })
    }
    return goods
  },

  /**
   * 计算各分类区域的位置
   */
  calculateCategoryTops() {
    const query = wx.createSelectorQuery()
    query.selectAll('.category-section').boundingClientRect()
    query.select('.goods-list').boundingClientRect()
    queres) => {
      if (res[0] && res[1]) {
        const sections = res[0]
        const container = res[1]
        const categoryTops = sections.map(item => item.top - container.top)
        this.setData({ categoryTops })
      }
    })
  },

  /**
   * 右侧商品列表滚动事件
   */
  onGoodsScroll(e) {
    if (this.scrollLock) return

    const { scrollTop, scrollHeight, deltaY } = e.detail
    const { categoryTops, currentCategory, categoryGoodsList } = this.data

    if (!categoryTops || categoryTops.length === 0) return

    // 找到当前滚动位置对应的分类
    let newCategory = 0
    for (let i = 0; i < categoryTops.length; i      if (scrollTop >= categoryTops 10) {
        newCategory = i
      } else {
        break
      }
    }

    if (newCategory !== currentCategory) {
      this.setData({
        currentCategory: newCategory,
        scrollToView: `left-category-${newCategory}`
      })

      // 懒加载：当滚动到新分类时，预加载下一个分类
      this.preloadNextCategories(newCategory)
    }

    // 检查是否需要加载当前分类的更多数据
    this.checkLoadMoreForCategory(newCategory, scrollTop)
  },

  /**
   * 预加载相邻分类的数据
   */
  preloadNextCategories(currentIndex) {
    const { categories } = this.data

    // 预加载当前分类及前后各1个分类
    const preloadIndexes = [
      currentIndex - 1,
      currentIndex,
      currentIndex + 1,
      currentIndex + 2
    ].filter(i => i >= 0 && i < categories.length)

    preloadIndexes.forEach(index => {
      if (!this.loadedCategories.has(index)) {
        this.loadCategoryGoods(index)
      }
    })
  },

  /**
   * 检查是否需要加载当前分类的更多商品
   */
  checkLoadMoreForCategory(categoryIndex, scrollTop) {
    const { categoryTops, categoryGoodsList } = this.data
    const category = categoryGoodsList[categoryIndex]

    if (!category || !category.hasMore || categorg) return

    // 计算当前分类区域的底部位置
    const nextCategoryTop = categoryTops[categoryIndex + 1]
    const currentCategoryTop = categoryTops[categoryIndex]

    if (nextCategoryTop) {
      // 当滚动到当前分类底部附近时，加载更多
      const threshold = nextCategoryTop - 200
      if (scrollTop > threshold - currentCategoryTop) {
        this.loadCategoryGoods(categoryIndex)
      }
    }
  },

  /**
   * 滚动到底部加载更多
   */
  onScrollToLower() {
    const { currentCategory, categoryGoodsList } = this.data
    const category = categoryGoodsList[currentCategory]

    if (category && category.hasMore && !category.loading) {
      this.loadCategoryGoods(currentCategory)
    }
  },

  /**
   * 点击左侧分类切换
   */
  switchCategory(e) {
    const index = e.currentTarget.dataset.index
    if (index === this.data.currentCategory) return

    this.scrollLock = true

    // 确保该分类已加载数据
    if (!this.loadedCategories.has(index)) {
      this.loadCategoryGoods(index).then(() => {
        this.scrollToCategory(index)
      })
    } else {
      this.scrollToCategory(index)
    }
  },

  /**
   * 滚动到指定分类
   */
  scrollToCategory(index) {
    this.setData({
      currentCategory: index,
      scrollToView: `left-category-${index}`
    })

    等待位置计算完成后滚动
    setTimeout(() => {
      const { categoryTops } = this.data
      if (categoryTops && categoryTops[index] !== undefined) {
        this.setData({
          rightScrollTop: this.data.rightScrollTop === categoryTops[index]
            ? categoryTops[index] + 0.1
            : categoryTops[index]
        })
      }

      setTimeout(() => {
        this.scrollLock = false
      }, 300)
    }, 100)
  },

  /**
   * 加载指定分类的更多商品（点击"加载更多"按钮）
   */
  loadMoreGoods(e) {
    const { index } = e.currentTarget.dataset
    this.loadCategoryGoods(index)
  },

  /**
   * 搜索
   */
  onSearch() {
    const { searchValue } = this.data
    if (!searchValue.trim()) {
      wx.showToast({ title: '请on: 'none' })
      return
    }
    wx.showToast({ title: '搜索: ' + searchValue, icon: 'none' })
  },

  onSearchChange(e) {
    this.setData({ searchValue: e.detail })
  },

  /**
   * 选择地址
   */
  chooseLocation() {
    wx.showToast({ title: '选择配送地址', icon: 'none' })
  },

  /**
   * 跳转商品详情
   */
  goToGoodsDetail(e) {
    const { goods } = e.currentTarget.dataset
    wx.showToast({ title: goods.name, icon: 'none' })
  },

  /**
   * 加入购物车
   */
  addToCart(e) {
    const { goods } = e.currentTarget.dataset
    const { cartList } = this.data

    const existIndex = cartList.findIndex(item => item.id === goods.id)

    if (existIndex > -1) {
      cartList[existIndex].count++
    } else {
      cartList.push({ ...goods, count: 1 })
    }

    this.setData({ cartList })
    this.calculateCart()
    this.saveCart()

    wx.showToast({ title: '已加入购物车', icon: 'success', duration: 1000 })
  },

  /**
   * 计算购物车
   */
  calculateCart() {
    const { cartList } = this.data
    let totalCount = 0
    let totalPrice = 0

    cartList.forEach(item => {
      totalCount += item.count
      totalPrice += parseFloat(item.price) * item.count
    })

    this.setData({
      cartTotalCount: totalCount,
      cartTotalPrice: totalPrice.toFixed(2)
    })
  },

  /**
   * 保存购物车到缓存
   */
  saveCart() {
    wx.setStorageSync('supermarket_cart', this.data.cartList)
  },

  /**
   * 显示购物车弹窗
   */
  showCartPopup() {
    if (this.data.cartList.length === 0) {
      wx.showToast({ title: '购物车是空的', icon: 'none' })
      return
    }
    this.setData({ showCart: true })
  },

  /**
   * 隐藏购物车弹窗
   */
  hideCartPopup() {
    this.setData({ showCart: false })
  },

  /**
   * 购物车商品数量变化
   */
  onCartItemChange(e) {
    const { id } = e.currentTarget.dataset
    const count = e.detail
    const { cartList } = this.data

    const index = cartList.findIndex(item => item.id === id)
    if (index === -1) return

    if (count === 0) {
      cartList.splice(index, 1)
    } else {
      cartList[index].count = count
    }

    this.setData({ cartList })
    this.calculateCart()
    this.saveCart()

    if (cartList.length === 0) {
      this.setData({ showCart: false })
    }
  },

  /**
   * 清空购物车
   */
  clearCart() {
    wx.showModal({
      title: '提示',
      content: '确定清空购物车吗？',
      success: (res) => {
        if (res.confirm) {
          this.setData({ cartList: [], showCart: false })
          this.calculateCart()
          this.saveCart()
          wx.showToast({ title: '已清空', icon: 'success' })
        }
      }
    })
  },

  /**
   * 提交订单
   */
  submitOrder() {
    const { cartTotalPrice, minOrderAmount } = this.data

    if (parseFloat(cartTotalPrice) < minOrderAmount) {
      wx.showToast({ title: `¥${minOrderAmount}起送`, icon: 'none' })
      re    }

    wx.showToast({ title: '去结算', icon: 'none' })
  },

  /**
   * 下拉刷新
   */
  onPullDownRefresh() {
    this.loadedCategories.clear()
    this.initCategoryPageInfo()
    this.loadInitialCategories()
    wx.stopPullDownRefresh()
  }
})

