// pages/rider/supermarket/index.js

Page({
  data: {
    // 搜索
    searchValue: '',
    currentLocation: '校园超市',

    // 分类
    categories: [
      { id: 1, name: '热销推荐', icon: '', badge: '' },
      { id: 2, name: '零食饮料', icon: '', badge: '热' },
      { id: 3, name: '方便速食', icon: '', badge: '' },
      { id: 4, name: '日用百货', icon: '', badge: '' },
      { id: 5, name: '个护美妆', icon: '', badge: '' },
      { id: 6, name: '文具办公', icon: '', badge: '' },
      { id: 7, name: '数码配件', icon: '', badge: '' },
      { id: 8, name: '生鲜水果', icon: '', badge: '新' },
      { id: 9, name: '乳品早餐', icon: '', badge: '' },
      { id: 10, name: '冷饮冰品', icon: '', badge: '' }
    ],
    currentCategory: 0,
    scrollToView: '',
    rightScrollTop: 0,

    // 商品列表（按分类分组）
    categoryGoodsList: [],
    loading: false,

    // 分类区域高度信息
    categoryTops: [],

    // 购物车
    cartList: [],
    cartTotalCount: 0,
    cartTotalPrice: '0.00',
    showCart: false,

    // 配送
    deliveryFee: 2,
    minOrderAmount: 15
  },

  // 滚动锁定标志
  scrollLock: false,
  // 每个分类的分页信息
  categoryPageInfo: {},
  // 每页加载数量
  pageSize: 10,
  // 初始加载的分类数量
  initialLoadCount: 3,
  // 已加载的分类索引集合
  loadedCategories: null,

  onLoad: function() {
    this.loadedCategories = new Set();
    this.initCategoryPageInfo();
    this.loadInitialCategories();
  },

  onShow: function() {
    var cartList = wx.getStorageSync('supermarket_cart') || [];
    this.setData({ cartList: cartList });
    this.calculateCart();
  },

  /**
   * 初始化分类分页信息
   */
  initCategoryPageInfo: function() {
    var categories = this.data.categories;
    this.categoryPageInfo = {};
    for (var i = 0; i < categories.length; i++) {
      this.categoryPageInfo[i] = {
        page: 1,
        hasMore: true,
        loading: false
      };
    }
  },

  /**
   * 初始加载前几个分类的数据
   */
  loadInitialCategories: function() {
    var that = this;
    this.setData({ loading: true });

    var categories = this.data.categories;
    var initialList = categories.map(function(category, index) {
      return {
        id: category.id,
        name: category.name,
        icon: category.icon,
        badge: category.badge,
        categoryIndex: index,
        goods: [],
        hasMore: true,
        loading: false
      };
    });

    this.setData({ categoryGoodsList: initialList });

    // 只加载前几个分类的第一页数据
    var loadCount = Math.min(this.initialLoadCount, categories.length);
    var loadedCount = 0;

    for (var i = 0; i < loadCount; i++) {
      this.loadCategoryGoods(i, true, function() {
        loadedCount++;
        if (loadedCount >= loadCount) {
          that.setData({ loading: false });
          setTimeout(function() {
            that.calculateCategoryTops();
          }, 300);
        }
      });
    }
  },

  /**
   * 加载指定分类的商品（支持分页）
   */
  loadCategoryGoods: function(categoryIndex, isInitial, callback) {
    var that = this;
    var pageInfo = this.categoryPageInfo[categoryIndex];

    if (!pageInfo || pageInfo.loading || (!isInitial && !pageInfo.hasMore)) {
      if (callback) callback();
      return;
    }

    pageInfo.loading = true;
    this.loadedCategories.add(categoryIndex);

    // 更新UI加载状态
    var loadingKey = 'categoryGoodsList[' + categoryIndex + '].loading';
    this.setData({ [loadingKey]: true });

    // 模拟API请求
    setTimeout(function() {
      var newGoods = that.fetchCategoryGoods(categoryIndex, pageInfo.page);
      var categoryGoodsList = that.data.categoryGoodsList;

      // 合并商品数据
      var currentGoods = categoryGoodsList[categoryIndex].goods || [];
      var updatedGoods = currentGoods.concat(newGoods);

      // 判断是否还有更多
      var hasMore = newGoods.length >= that.pageSize;

      // 更新数据
      var updateData = {};
      updateData['categoryGoodsList[' + categoryIndex + '].goods'] = updatedGoods;
      updateData['categoryGoodsList[' + categoryIndex + '].hasMore'] = hasMore;
      updateData['categoryGoodsList[' + categoryIndex + '].loading'] = false;
      that.setData(updateData);

      // 更新分页信息
      pageInfo.page++;
      pageInfo.hasMore = hasMore;
      pageInfo.loading = false;

      // 重新计算位置
      setTimeout(function() {
        that.calculateCategoryTops();
      }, 100);

      if (callback) callback();
    }, 300);
  },

  /**
   * 模拟获取分类商品数据（实际项目中替换为API调用）
   */
  fetchCategoryGoods: function(categoryIndex, page) {
    var that = this;
    // 模拟数据源 - 每个分类有不同数量的商品
    var totalCounts = [50, 40, 35, 45, 25, 30, 20, 28, 22, 18];
    var prefixes = ['热销', '零食', '速食', '日用', '个护', '文具', '数码', '水果', '乳品', '冷饮'];
    var startIds = [101, 201, 301, 401, 501, 601, 701, 801, 901, 1001];

    var total = totalCounts[categoryIndex] || 20;
    var prefix = prefixes[categoryIndex] || '商品';
    var startId = startIds[categoryIndex] || 1;

    var allGoods = this.generateMockGoods(prefix, startId, total);
    var start = (page - 1) * this.pageSize;
    var end = start + this.pageSize;

    return allGoods.slice(start, end);
  },

  /**
   * 生成模拟商品数据
   */
  generateMockGoods: function(prefix, startId, total) {
    var goods = [];
    var tags = ['热销', '新品', '爆款', '特价', ''];
    var images = [
      'https://img.alicdn.com/imgextra/i4/2616970884/O1CN01FpfRzj1IOuv0xPJKT_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i3/2616970884/O1CN01qNxfnB1IOuuzWJHxK_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i1/2616970884/O1CN01Ej5Yzd1IOuv2OQHQE_!!2616970884.jpg',
      'https://img.alicdn.com/imgextra/i2/2616970884/O1CN01xNxfnB1IOuuzWJHxL_!!2616970884.jpg'
    ];

    for (var i = 0; i < total; i++) {
      var price = (Math.random() * 50 + 2).toFixed(2);
      var hasOriginal = Math.random() > 0.5;
      goods.push({
        id: startId + i,
        name: prefix + '商品' + (i + 1),
        price: price,
        originalPrice: hasOriginal ? (parseFloat(price) * 1.2).toFixed(2) : '',
        image: images[i % images.length],
        tag: tags[Math.floor(Math.random() * tags.length)],
        sales: Math.floor(Math.random() * 1000),
        desc: prefix + '商品描述' + (i + 1)
      });
    }
    return goods;
  },

  /**
   * 计算各分类区域的位置
   */
  calculateCategoryTops: function() {
    var that = this;
    var query = wx.createSelectorQuery();
    query.selectAll('.category-section').boundingClientRect();
    query.select('.goods-list').boundingClientRect();
    query.exec(function(res) {
      if (res[0] && res[1]) {
        var sections = res[0];
        var container = res[1];
        var categoryTops = sections.map(function(item) {
          return item.top - container.top;
        });
        that.setData({ categoryTops: categoryTops });
      }
    });
  },

  /**
   * 右侧商品列表滚动事件
   */
  onGoodsScroll: function(e) {
    if (this.scrollLock) return;

    var scrollTop = e.detail.scrollTop;
    var categoryTops = this.data.categoryTops;
    var currentCategory = this.data.currentCategory;

    if (!categoryTops || categoryTops.length === 0) return;

    // 找到当前滚动位置对应的分类
    var newCategory = 0;
    for (var i = 0; i < categoryTops.length; i++) {
      if (scrollTop >= categoryTops[i] - 10) {
        newCategory = i;
      } else {
        break;
      }
    }

    if (newCategory !== currentCategory) {
      this.setData({
        currentCategory: newCategory,
        scrollToView: 'left-category-' + newCategory
      });

      // 懒加载：当滚动到新分类时，预加载下一个分类
      this.preloadNextCategories(newCategory);
    }

    // 检查是否需要加载当前分类的更多数据
    this.checkLoadMoreForCategory(newCategory, scrollTop);
  },

  /**
   * 预加载相邻分类的数据
   */
  preloadNextCategories: function(currentIndex) {
    var that = this;
    var categories = this.data.categories;

    // 预加载当前分类及前后各1个分类
    var preloadIndexes = [
      currentIndex - 1,
      currentIndex,
      currentIndex + 1,
      currentIndex + 2
    ].filter(function(i) {
      return i >= 0 && i < categories.length;
    });

    preloadIndexes.forEach(function(index) {
      if (!that.loadedCategories.has(index)) {
        that.loadCategoryGoods(index, false);
      }
    });
  },

  /**
   * 检查是否需要加载当前分类的更多商品
   */
  checkLoadMoreForCategory: function(categoryIndex, scrollTop) {
    var categoryTops = this.data.categoryTops;
    var categoryGoodsList = this.data.categoryGoodsList;
    var category = categoryGoodsList[categoryIndex];

    if (!category || !category.hasMore || category.loading) return;

    // 计算当前分类区域的底部位置
    var nextCategoryTop = categoryTops[categoryIndex + 1];
    var currentCategoryTop = categoryTops[categoryIndex];

    if (nextCategoryTop) {
      // 当滚动到当前分类底部附近时，加载更多
      var threshold = nextCategoryTop - 200;
      if (scrollTop > threshold - currentCategoryTop) {
        this.loadCategoryGoods(categoryIndex, false);
      }
    }
  },

  /**
   * 滚动到底部加载更多
   */
  onScrollToLower: function() {
    var currentCategory = this.data.currentCategory;
    var categoryGoodsList = this.data.categoryGoodsList;
    var category = categoryGoodsList[currentCategory];

    if (category && category.hasMore && !category.loading) {
      this.loadCategoryGoods(currentCategory, false);
    }
  },

  /**
   * 点击左侧分类切换
   */
  switchCategory: function(e) {
    var that = this;
    var index = e.currentTarget.dataset.index;
    if (index === this.data.currentCategory) return;

    this.scrollLock = true;

    // 确保该分类已加载数据
    if (!this.loadedCategories.has(index)) {
      this.loadCategoryGoods(index, false, function() {
        that.scrollToCategory(index);
      });
    } else {
      this.scrollToCategory(index);
    }
  },

  /**
   * 滚动到指定分类
   */
  scrollToCategory: function(index) {
    var that = this;
    this.setData({
      currentCategory: index,
      scrollToView: 'left-category-' + index
    });

    // 等待位置计算完成后滚动
    setTimeout(function() {
      var categoryTops = that.data.categoryTops;
      if (categoryTops && categoryTops[index] !== undefined) {
        that.setData({
          rightScrollTop: that.data.rightScrollTop === categoryTops[index]
            ? categoryTops[index] + 0.1
            : categoryTops[index]
        });
      }

      setTimeout(function() {
        that.scrollLock = false;
      }, 300);
    }, 100);
  },

  /**
   * 加载指定分类的更多商品（点击"加载更多"按钮）
   */
  loadMoreGoods: function(e) {
    var index = e.currentTarget.dataset.index;
    this.loadCategoryGoods(index, false);
  },

  /**
   * 搜索
   */
  onSearch: function() {
    var searchValue = this.data.searchValue;
    if (!searchValue.trim()) {
      wx.showToast({ title: '请输入搜索内容', icon: 'none' });
      return;
    }
    wx.showToast({ title: '搜索: ' + searchValue, icon: 'none' });
  },

  onSearchChange: function(e) {
    this.setData({ searchValue: e.detail });
  },

  /**
   * 选择地址
   */
  chooseLocation: function() {
    wx.showToast({ title: '选择配送地址', icon: 'none' });
  },

  /**
   * 跳转商品详情
   */
  goToGoodsDetail: function(e) {
    var goods = e.currentTarget.dataset.goods;
    wx.showToast({ title: goods.name, icon: 'none' });
  },

  /**
   * 加入购物车
   */
  addToCart: function(e) {
    var goods = e.currentTarget.dataset.goods;
    var cartList = this.data.cartList;

    var existIndex = -1;
    for (var i = 0; i < cartList.length; i++) {
      if (cartList[i].id === goods.id) {
        existIndex = i;
        break;
      }
    }

    if (existIndex > -1) {
      cartList[existIndex].count++;
    } else {
      var newItem = {
        id: goods.id,
        name: goods.name,
        price: goods.price,
        image: goods.image,
        count: 1
      };
      cartList.push(newItem);
    }

    this.setData({ cartList: cartList });
    this.calculateCart();
    this.saveCart();

    wx.showToast({ title: '已加入购物车', icon: 'success', duration: 1000 });
  },

  /**
   * 计算购物车
   */
  calculateCart: function() {
    var cartList = this.data.cartList;
    var totalCount = 0;
    var totalPrice = 0;

    for (var i = 0; i < cartList.length; i++) {
      totalCount += cartList[i].count;
      totalPrice += parseFloat(cartList[i].price) * cartList[i].count;
    }

    this.setData({
      cartTotalCount: totalCount,
      cartTotalPrice: totalPrice.toFixed(2)
    });
  },

  /**
   * 保存购物车到缓存
   */
  saveCart: function() {
    wx.setStorageSync('supermarket_cart', this.data.cartList);
  },

  /**
   * 显示购物车弹窗
   */
  showCartPopup: function() {
    if (this.data.cartList.length === 0) {
      wx.showToast({ title: '购物车是空的', icon: 'none' });
      return;
    }
    this.setData({ showCart: true });
  },

  /**
   * 隐藏购物车弹窗
   */
  hideCartPopup: function() {
    this.setData({ showCart: false });
  },

  /**
   * 购物车商品数量变化
   */
  onCartItemChange: function(e) {
    var id = e.currentTarget.dataset.id;
    var count = e.detail;
    var cartList = this.data.cartList;

    var index = -1;
    for (var i = 0; i < cartList.length; i++) {
      if (cartList[i].id === id) {
        index = i;
        break;
      }
    }
    if (index === -1) return;

    if (count === 0) {
      cartList.splice(index, 1);
    } else {
      cartList[index].count = count;
    }

    this.setData({ cartList: cartList });
    this.calculateCart();
    this.saveCart();

    if (cartList.length === 0) {
      this.setData({ showCart: false });
    }
  },

  /**
   * 清空购物车
   */
  clearCart: function() {
    var that = this;
    wx.showModal({
      title: '提示',
      content: '确定清空购物车吗？',
      success: function(res) {
        if (res.confirm) {
          that.setData({ cartList: [], showCart: false });
          that.calculateCart();
          that.saveCart();
          wx.showToast({ title: '已清空', icon: 'success' });
        }
      }
    });
  },

  /**
   * 提交订单
   */
  submitOrder: function() {
    var cartTotalPrice = this.data.cartTotalPrice;
    var minOrderAmount = this.data.minOrderAmount;

    if (parseFloat(cartTotalPrice) < minOrderAmount) {
      wx.showToast({ title: '¥' + minOrderAmount + '起送', icon: 'none' });
      return;
    }

    wx.showToast({ title: '去结算', icon: 'none' });
  },

  /**
   * 下拉刷新
   */
  onPullDownRefresh: function() {
    this.loadedCategories.clear();
    this.initCategoryPageInfo();
    this.loadInitialCategories();
    wx.stopPullDownRefresh();
  }
});

